import * as d3 from 'd3';

let hoverTimeout;

export const addMouseEvents = (g) => {
  g.selectAll('rect').each(function () {
    const rect = d3.select(this);
    const originalSize = { width: +rect.attr('width'), height: +rect.attr('height') };
    const originalPos = { x: +rect.attr('x'), y: +rect.attr('y') };

    rect.on('mouseover', function (event) {
      if (rect.attr('id') === 'rect1') return;

      hoverTimeout = setTimeout(() => {
        showInfoPanel(event, rect);
      }, 1500);

      rect
        .attr('fill-opacity', 1)
        .attr('stroke-opacity', 1)
        .transition()
        .duration(200)
        .attr('width', originalSize.width * 2)
        .attr('height', originalSize.height * 2)
        .attr('x', originalPos.x - originalSize.width / 2)
        .attr('y', originalPos.y - originalSize.height / 2);
    }).on('mouseout', function () {
      if (rect.attr('id') === 'rect1') return;

      clearTimeout(hoverTimeout);
      hideInfoPanel();

      rect.transition()
        .duration(200)
        .attr('width', originalSize.width)
        .attr('height', originalSize.height)
        .attr('x', originalPos.x)
        .attr('y', originalPos.y)
        .attr('fill-opacity', 0.8);
    });
  });
};

const showInfoPanel = (event, rect) => {
  const infoPanel = d3.select('#info-panel');
  const systemId = rect.attr('id');
  const systemName = `System ${systemId}`;
  const planetList = 'PlanetA, PlanetB, PlanetC';

  infoPanel
    .style('display', 'block')
    .style('left', `${event.pageX + 10}px`)
    .style('top', `${event.pageY + 10}px`)
    .html(`
      <strong>System name:</strong> ${systemName}<br/>
      <strong>Planet list:</strong> ${planetList}
    `);
};

const hideInfoPanel = () => {
  d3.select('#info-panel').style('display', 'none');
};
