import React, { createContext, useState, useCallback, useContext } from 'react';
import { GraphContext } from './GraphContext';
import { highlightSelectedSystem as highlightSelectedSystemUtil } from '../utils/graphUtils';


export const SelectionContext = createContext();

export const SelectionProvider = ({ children }) => {
  const [isPathfindingEnabled, setIsPathfindingEnabled] = useState(false);
  const [selectedSystem, setSelectedSystem] = useState(null);
  //const { highlightSelectedSystem } = useContext(GraphContext);

  const togglePathfinding = useCallback(() => {
    setIsPathfindingEnabled(prev => !prev);
  }, []);

  const setAndHighlightSelectedSystem = useCallback((nextSelectedSystem) => {
    setSelectedSystem(prevSelectedSystem => {
      highlightSelectedSystem(prevSelectedSystem, nextSelectedSystem);
      return nextSelectedSystem;
    });
  }, [highlightSelectedSystem]);

  const highlightSelectedSystem = useCallback((prevSelectedSystem, nextSelectedSystem) => {
    highlightSelectedSystemUtil(prevSelectedSystem, nextSelectedSystem);
  }, []);

  return (
    <SelectionContext.Provider
      value={{
        isPathfindingEnabled,
        togglePathfinding,
        selectedSystem,
        setSelectedSystem: setAndHighlightSelectedSystem
      }}
    >
      {children}
    </SelectionContext.Provider>
  );
};
