import React, { useState, useContext } from 'react';
import { ChevronRight, ChevronLeft } from 'lucide-react';
import { GraphContext } from '../contexts/GraphContext';

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const { selectedSystem, planetData } = useContext(GraphContext);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  const planets = selectedSystem ? planetData[selectedSystem] : null;
  console.log('Grouped Data:', planets);
  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <button className="toggle-btn" onClick={toggleSidebar}>
        {isCollapsed ? <ChevronLeft /> : <ChevronRight />}
      </button>
      {!isCollapsed && (
        <div className="sidebar-content">
          <h2>{selectedSystem ? selectedSystem.Name : 'No System Selected'}</h2>
          {planets && planets.map((planet, index) => (
            <div key={index} className="planet-info">
              <h3>{planet.PlanetName} ({planet.PlanetNaturalId})</h3>
              <p>Resources:</p>
              <ul>
                {planet.Resources.map((resource, idx) => (
                  <li key={idx}>{resource.MaterialId}: {(resource.Factor * 100).toFixed(2)}%</li>
                ))}
              </ul>
              <p>Facilities:</p>
              <ul>
                <li>Local Market: {planet.HasLocalMarket ? 'Yes' : 'No'}</li>
                <li>Chamber of Commerce: {planet.HasChamberOfCommerce ? 'Yes' : 'No'}</li>
                <li>Warehouse: {planet.HasWarehouse ? 'Yes' : 'No'}</li>
                <li>Administration Center: {planet.HasAdministrationCenter ? 'Yes' : 'No'}</li>
                <li>Shipyard: {planet.HasShipyard ? 'Yes' : 'No'}</li>
              </ul>
              {planet.COGCProgramStatus === "ACTIVE" && planet.COGCPrograms && planet.COGCPrograms.length > 0 && (
                <div>
                  <p>COGC Program: {planet.COGCPrograms[0].ProgramType}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Sidebar;
