import React, { useEffect, useState, useCallback } from 'react';
import * as d3 from 'd3';
import { find_path } from 'dijkstrajs';
import './UniverseMap.css'; // Import the CSS file

const UniverseMap = () => {
  const [graph, setGraph] = useState({ nodes: {}, edges: [] });
  const [selectedSystems, setSelectedSystems] = useState([]);

  useEffect(() => {
    fetch('/graph_data.json')
      .then(response => response.json())
      .then(data => setGraph(data));
  }, []);

  const findShortestPath = useCallback((system1, system2) => {
    if (system1 === 'rect1' || system2 === 'rect1') {
      console.error('Invalid system selection for pathfinding:', system1, system2);
      return;
    }

    const graphNodes = {};
    graph.edges.forEach(edge => {
      if (!graphNodes[edge.start]) graphNodes[edge.start] = {};
      if (!graphNodes[edge.end]) graphNodes[edge.end] = {};
      graphNodes[edge.start][edge.end] = edge.distance;
      graphNodes[edge.end][edge.start] = edge.distance;
    });

    try {
      const path = find_path(graphNodes, system1, system2);
      highlightPath(path);
    } catch (error) {
      console.error('Error finding path:', error);
    }
  }, [graph]);

  const highlightPath = (path) => {
    // Reset all system nodes color and stroke except the background rect
    d3.selectAll('rect').each(function() {
      if (d3.select(this).attr('id') !== 'rect1') {
        d3.select(this).attr('style', 'fill:#cccccc;fill-opacity:0.8;stroke:#000000;stroke-width:1px');
      }
    });

    // Highlight systems in the path
    path.forEach(system => {
      const element = d3.select(`#${CSS.escape(system)}`);

      // Get the existing style attribute
      const style = element.attr('style');

      // Parse the style string and update the fill and stroke values
      const styleObj = style.split(';').reduce((acc, item) => {
        const [key, value] = item.split(':');
        if (key && value) {
          acc[key.trim()] = value.trim();
        }
        return acc;
      }, {});

      styleObj.fill = '#ff0000'; // Set fill to red
      styleObj.stroke = '#ffff00'; // Set stroke to yellow

      // Convert the style object back to a string
      const newStyle = Object.entries(styleObj).map(([key, value]) => `${key}:${value}`).join(';');

      // Update the element's style attribute
      element.attr('style', newStyle);
    });
  };

  useEffect(() => {
    let svgNode;

    d3.xml('/PrUn_universe_map.svg').then(data => {
      svgNode = data.documentElement;
      const container = document.getElementById('map-container');

      // Clear any existing SVG to prevent duplicates
      while (container.firstChild) {
        container.removeChild(container.firstChild);
      }

      // Append the SVG node to the container
      container.appendChild(svgNode);

      // Select the SVG element and create a group element
      const svg = d3.select(svgNode);
      const g = svg.append('g');

      // Move all children of the SVG to the group
      while (svgNode.firstChild && svgNode.firstChild !== g.node()) {
        g.node().appendChild(svgNode.firstChild);
      }

      const zoom = d3.zoom()
        .scaleExtent([1, 3]) // Set the minimum and maximum zoom scales
        .on('zoom', (event) => {
          g.attr('transform', event.transform);
        });

      svg.call(zoom);

      // Add click event to systems (assuming they are rect elements)
      g.selectAll('rect').on('click', function() {
        const systemId = d3.select(this).attr('id').replace('#', '');
        if (systemId === 'rect1') {
          return;
        }
        setSelectedSystems(prev => {
          const newSelection = [...prev, systemId];
          if (newSelection.length === 2) {
            findShortestPath(newSelection[0], newSelection[1]);
            return [];
          }
          return newSelection;
        });
      });
    });

    return () => {
      if (svgNode) {
        const container = document.getElementById('map-container');
        container.removeChild(svgNode);
      }
    };
  }, [graph, findShortestPath]);

  return <div id="map-container"></div>;
};

export default UniverseMap;
