import * as d3 from 'd3';
import { resetGraphState } from '../utils/graphUtils';

export const highlightSearchResults = (graphRef, searchResults) => {
  if (graphRef.current && searchResults.length > 0) {
    const { g } = graphRef.current;

    // Reset all systems to default state
    resetGraphState();

    console.log('d3', d3)
    // Highlight systems from search results
    searchResults.forEach(result => {
      if (result.type === 'system') {
        g.select(`#${result.id}`)
          .attr('fill', '#f7a600')
          .attr('stroke', '#ff0000')
          .attr('stroke-width', 2);
      } else if (result.type === 'planet') {
        g.select(`#${result.systemId}`)
          .attr('fill', '#f7a600')
          .attr('stroke', '#00ff00')
          .attr('stroke-width', 2);
      }
    });
  }
};

export const clearHighlights = (graphRef) => {
  if (graphRef.current) {
    const { g } = graphRef.current;
    g.selectAll('rect')
      .attr('fill', '#cccccc')
      .attr('stroke', '#000000')
      .attr('stroke-width', 1);
  }
};
