import React, { useState, useContext } from 'react';
import { ChevronRight, ChevronLeft } from 'lucide-react';
import { GraphContext } from '../contexts/GraphContext';

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const { selectedSystem, planets } = useContext(GraphContext);

  // Add logging for debugging
  console.log('Selected System:', selectedSystem);
  console.log('Planets:', planets);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <button className="toggle-btn" onClick={toggleSidebar}>
        {isCollapsed ? <ChevronLeft /> : <ChevronRight />}
      </button>
      {!isCollapsed && (
        <div className="sidebar-content">
          <h2>{selectedSystem ? selectedSystem.Name : 'No System Selected'}</h2>
          {planets && planets.map((planet, index) => (
            <div key={index} className="planet-info">
              <h3>{planet.Name}</h3>
              <p>Resources:</p>
              <ul>
                {planet.Resources && planet.Resources.map((resource, idx) => (
                  <li key={idx}>{resource.Name}: {resource.Concentration}%</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Sidebar;
