import React, { useEffect, useContext } from 'react';
import * as d3 from 'd3';
import { GraphContext } from '../contexts/GraphContext';
import { addMouseEvents } from '../utils/svgUtils';
import './UniverseMap.css';

const UniverseMap = () => {
  const { graph, fetchGraphData } = useContext(GraphContext);

  useEffect(() => {
    usefetchGraphData();
  }, [usefetchGraphData]);

  useEffect(() => {
    let svgNode;
    let zoom;

    d3.xml('/PrUn_universe_map_normalized.svg').then(data => {
      svgNode = data.documentElement;
      const container = document.getElementById('map-container');

      // Clear any existing SVG to prevent duplicates
      while (container.firstChild) {
        container.removeChild(container.firstChild);
      }

      // Append the SVG node to the container
      container.appendChild(svgNode);

      // Select the SVG element and create a group element
      const svg = d3.select(svgNode);
      const g = svg.append('g');

      // Move all children of the SVG to the group
      while (svgNode.firstChild && svgNode.firstChild !== g.node()) {
        g.node().appendChild(svgNode.firstChild);
      }

      zoom = d3.zoom()
        .scaleExtent([1, 20]) // Set the minimum and maximum zoom scales
        .on('zoom', (event) => {
          g.attr('transform', event.transform);
        });

      svg.call(zoom);

      // Add mouse events for nodes
      addMouseEvents(g);

    });

    return () => {
      if (svgNode) {
        const container = document.getElementById('map-container');
        if (container.contains(svgNode)) {
          container.removeChild(svgNode);
        }
      }
    };
  }, [graph]);

  return (
    <div id="map-container">
      <div id="info-panel" className="info-panel"></div>
    </div>
  );
};

export default UniverseMap;
